#nullable enable // autogenerated

using System;
using PVZEngine.Models;

namespace PVZEngine.Entities
{
    public sealed partial class Entity
    {
        #region 模型
        public void SetModelInterface(IModelInterface? model)
        {
            modelInterface = model;
        }
        public IModelInterface? GetModelInterface()
        {
            return modelInterface;
        }
        public void ChangeModel(NamespaceID modelID)
        {
            ModelID = modelID;
            OnChangeModel?.Invoke(modelID);
        }
        #endregion

        #region 序列化
        private void LoadModelFromSerializable(SerializableEntity seri)
        {
            ModelID = seri.modelID ?? Definition.GetID();
        }
        #endregion

        #region 事件
        public event Action<NamespaceID>? OnChangeModel;
        #endregion

        #region 属性字段
        public NamespaceID ModelID { get; private set; }
        private IModelInterface? modelInterface;
        #endregion
    }
}