#nullable enable // autogenerated

namespace PVZEngine.Modifiers
{
    public class BooleanModifier : PropertyModifier<bool>
    {
        public BooleanModifier(PropertyKey<bool> propertyName, bool valueConst, int priority = 0) : this(propertyName, BooleanOperator.Set, valueConst, priority)
        {
        }

        public BooleanModifier(PropertyKey<bool> propertyName, PropertyKey<bool> buffPropertyName, int priority = 0) : this(propertyName, BooleanOperator.Set, buffPropertyName, priority)
        {
        }
        public BooleanModifier(PropertyKey<bool> propertyName, BooleanOperator op, bool valueConst, int priority = 0) : base(propertyName, valueConst, priority)
        {
            Operator = op;
        }

        public BooleanModifier(PropertyKey<bool> propertyName, BooleanOperator op, PropertyKey<bool> buffPropertyName, int priority = 0) : base(propertyName, buffPropertyName, priority)
        {
            Operator = op;
        }
        public BooleanOperator Operator { get; private set; }
        public override ModifierCalculator GetCalculator()
        {
            return CalculatorMap.booleanCalculator;
        }
    }
}
