#nullable enable // autogenerated

namespace PVZEngine.Modifiers
{
    public abstract class IntegerModifier<T> : PropertyModifier<T>
    {
        public IntegerModifier(PropertyKey<T> propertyName, IntegerOperator op, T constValue, int priority = 0) : base(propertyName, constValue, priority)
        {
            Operator = op;
        }

        public IntegerModifier(PropertyKey<T> propertyName, IntegerOperator op, PropertyKey<T> buffPropertyName, int priority = 0) : base(propertyName, buffPropertyName, priority)
        {
            Operator = op;
        }

        public IntegerOperator Operator { get; private set; }
    }
}
