#nullable enable // autogenerated

using UnityEngine;

namespace PVZEngine
{
    public static class Ticks
    {
        private static int tps = 30;
        public static void SetTPS(int tps)
        {
            Ticks.tps = tps;
        }
        public static int GetTPS() => tps;
        public static int FromSeconds(float seconds)
        {
            return Mathf.FloorToInt(seconds * tps);
        }
        public static float ToSeconds(int ticks)
        {
            return (float)ticks / tps;
        }
        public static float FromPerSecond(float perSecond)
        {
            return perSecond / tps;
        }
        public static float ToPerSecond(float perTick)
        {
            return perTick * tps;
        }
        public static float SmoothDamp(float current, float target, float damp, int targetTPS = 30)
        {
            var newDamp = 1 - Mathf.Pow(1 - damp, targetTPS / tps);
            current += (target - current) * newDamp;
            return current;
        }
    }
}
