#nullable enable // autogenerated

using PVZEngine;
using PVZEngine.Armors;

namespace MVZ2.Vanilla.Entities
{
    [PropertyRegistryRegion(PropertyRegions.armor)]
    public static class LogicArmorProps
    {
        private static PropertyMeta<T> Get<T>(string name)
        {
            return new PropertyMeta<T>(name);
        }
        public static readonly PropertyMeta<NamespaceID> ARMOR_TYPE = Get<NamespaceID>("armor_type");
        public static NamespaceID? GetArmorType(this ArmorDefinition armor)
        {
            return armor.GetProperty<NamespaceID>(ARMOR_TYPE);
        }
        public static void SetArmorType(this ArmorDefinition armor, NamespaceID? value)
        {
            armor.SetProperty<NamespaceID>(ARMOR_TYPE, value);
        }
    }
}

