#nullable enable // autogenerated

using MVZ2.HeldItems;
using MVZ2Logic.Games;
using MVZ2Logic.Level;
using PVZEngine.Entities;
using PVZEngine.Level;

namespace MVZ2Logic.HeldItems
{
    public static partial class LogicHeldItemExt
    {
        public static HeldItemDefinition? GetDefinition(this IHeldItemData data, LevelEngine level)
        {
            return level.Content.GetHeldItemDefinition(data.Type);
        }
        public static Entity? GetHoldingEntity(this IHeldItemData data, LevelEngine level)
        {
            var entityID = data.GetEntityID();
            return level.FindEntityByID(entityID);
        }
    }
}
