#nullable enable // autogenerated

using System;
using PVZEngine.Base;

namespace MVZ2Logic.Saves
{
    public class UserStatDirectEntry
    {
        public UserStatDirectEntry(string id)
        {
            ID = id;
        }
        public SerializableUserStatDirectEntry ToSerializable()
        {
            return new SerializableUserStatDirectEntry()
            {
                id = ID,
                value = Value
            };
        }
        public static UserStatDirectEntry? FromSerializable(SerializableUserStatDirectEntry serializable)
        {
            if (string.IsNullOrEmpty(serializable.id))
            {
                Log.LogException(MissingSerializeDataException.Property<UserStatDirectEntry>(nameof(serializable.id)));
                return null;
            }
            return new UserStatDirectEntry(serializable.id)
            {
                Value = serializable.value
            };
        }
        public string ID { get; private set; }
        public long Value { get; set; }
    }
    [Serializable]
    public class SerializableUserStatDirectEntry
    {
        public string? id;
        public long value;
    }
}
