#nullable enable // autogenerated

using MongoDB.Bson;
using MongoDB.Bson.Serialization;
using Tools.BsonSerializers;

namespace MVZ2Logic.BsonSerializers
{
    public class SpriteReferenceSerializer : WrappedSerializerBase<SpriteReference>
    {
        public SpriteReferenceSerializer(string defaultNsp)
        {
            this.defaultNsp = defaultNsp;
        }
        protected override void SerializeValue(BsonSerializationContext context, BsonSerializationArgs args, SpriteReference value)
        {
            var writer = context.Writer;
            if (value == null)
            {
                writer.WriteNull();
            }
            else
            {
                writer.WriteString(value.ToString());
            }
        }

        protected override SpriteReference DeserializeClassValue(BsonDeserializationContext context, BsonDeserializationArgs args)
        {
            var reader = context.Reader;

            var bsonType = reader.GetCurrentBsonType();
            switch (bsonType)
            {
                case BsonType.String:
                    if (SpriteReference.TryParse(reader.ReadString(), defaultNsp, out var parsed))
                    {
                        return parsed;
                    }
                    return null!;

                default:
                    throw CreateCannotDeserializeFromBsonTypeException(bsonType);
            }
        }
        private string defaultNsp;
    }
}
