#nullable enable // autogenerated

using MVZ2.Models;
using UnityEngine;

namespace MVZ2.Almanacs
{
    public class AlmanacModel : MonoBehaviour
    {
        public void ChangeModel(ModelBuilder builder)
        {
            if (model)
            {
                Destroy(model.gameObject);
                model = null;
                updater.model = null;
            }
            model = builder.Build(rootTransform);
            if (model)
            {
                updater.model = model;
                if (model is EntityModel spriteModel)
                {
                    spriteModel.CancelSortAtRoot();
                }
            }
        }
        [SerializeField]
        private Transform rootTransform;
        [SerializeField]
        private ModelUpdater updater;
        [SerializeField]
        private Model model;
    }
}
