#nullable enable // autogenerated

using TMPro;
using UnityEngine;

namespace MVZ2.Mainmenu
{
    public class StatDirectEntryUI : MonoBehaviour
    {
        public void UpdateEntry(StatDirectEntryViewData viewData)
        {
            nameText.text = viewData.name;
            numberText.text = viewData.number;
        }
        [SerializeField]
        private TextMeshProUGUI nameText = null!;
        [SerializeField]
        private TextMeshProUGUI numberText = null!;
    }
    public struct StatDirectEntryViewData
    {
        public string name;
        public string number;
    }
}
