#nullable enable // autogenerated

using System.Xml;
using MVZ2.IO;

namespace MVZ2.Metas
{
    public class ArchiveTagMeta
    {
        public ArchiveTagMeta(string id)
        {
            ID = id;
        }

        public string ID { get; private set; }
        public string Name { get; private set; } = string.Empty;
        public int Priority { get; private set; }
        public static ArchiveTagMeta? FromXmlNode(XmlNode node, string defaultNsp)
        {
            var id = node.GetAttribute("id");
            if (string.IsNullOrEmpty(id))
            {
                Log.LogError("The ID of an ArchiveTagMeta is invalid.");
                return null;
            }
            var name = node.GetAttribute("name") ?? string.Empty;
            var priority = node.GetAttributeInt("priority") ?? 0;
            return new ArchiveTagMeta(id)
            {
                Name = name,
                Priority = priority,
            };
        }
    }
}
