#nullable enable // autogenerated

using System.Collections.Generic;
using System.Xml;

namespace MVZ2.Metas
{
    public class ArmorMetaList
    {
        public ArmorSlotMeta[] slots;
        public ArmorMeta[] metas;

        public ArmorMetaList(ArmorSlotMeta[] slots, ArmorMeta[] metas)
        {
            this.slots = slots;
            this.metas = metas;
        }

        public static ArmorMetaList FromXmlNode(XmlNode node, string defaultNsp)
        {
            var slotsNode = node["slots"];
            var slots = new List<ArmorSlotMeta>();
            if (slotsNode != null)
            {
                for (int i = 0; i < slotsNode.ChildNodes.Count; i++)
                {
                    var meta = ArmorSlotMeta.FromXmlNode(slotsNode.ChildNodes[i], defaultNsp);
                    if (meta != null)
                    {
                        slots.Add(meta);
                    }
                }
            }

            var entriesNode = node["entries"];
            var resources = new List<ArmorMeta>();
            if (entriesNode != null)
            {
                for (int i = 0; i < entriesNode.ChildNodes.Count; i++)
                {
                    var meta = ArmorMeta.FromXmlNode(entriesNode.ChildNodes[i], defaultNsp);
                    if (meta != null)
                    {
                        resources.Add(meta);
                    }
                }
            }
            return new ArmorMetaList(slots.ToArray(), resources.ToArray());
        }
    }
}
