#nullable enable // autogenerated

using System.Collections.Generic;
using System.Xml;

namespace MVZ2.Metas
{
    public class ChapterTransitionMetaList
    {
        public ChapterTransitionMetaList(ChapterTransitionMeta[] metas)
        {
            Metas = metas;
        }

        public ChapterTransitionMeta[] Metas { get; private set; }
        public static ChapterTransitionMetaList FromXmlNode(XmlNode node, string defaultNsp)
        {
            var tags = new List<ChapterTransitionMeta>();
            for (var i = 0; i < node.ChildNodes.Count; i++)
            {
                var child = node.ChildNodes[i];
                if (child.Name == "transition")
                {
                    var meta = ChapterTransitionMeta.FromXmlNode(child, defaultNsp);
                    if (meta != null)
                        tags.Add(meta);
                }
            }
            return new ChapterTransitionMetaList(tags.ToArray());
        }
    }
}
