#nullable enable // autogenerated

using System.Collections.Generic;
using System.Xml;

namespace MVZ2.Metas
{
    public class FragmentMetaList
    {
        public FragmentMeta[] metas;

        public FragmentMetaList(FragmentMeta[] metas)
        {
            this.metas = metas;
        }

        public static FragmentMetaList FromXmlNode(XmlNode node)
        {
            var resources = new List<FragmentMeta>();
            for (int i = 0; i < node.ChildNodes.Count; i++)
            {
                var meta = FragmentMeta.FromXmlNode(node.ChildNodes[i]);
                if (meta != null)
                    resources.Add(meta);
            }
            return new FragmentMetaList(resources.ToArray());
        }
    }
}
