#nullable enable // autogenerated

using System.Collections.Generic;
using System.Xml;

namespace MVZ2.Metas
{
    public class ModelMetaList
    {
        public ModelMeta[] metas;
        public ModelArmorConfigMeta[] armorConfigs;

        public ModelMetaList(ModelMeta[] metas, ModelArmorConfigMeta[] armorConfigs)
        {
            this.metas = metas;
            this.armorConfigs = armorConfigs;
        }

        public static ModelMetaList FromXmlNode(XmlNode node, string defaultNsp)
        {
            var metas = new List<ModelMeta>();
            var armorConfigs = new List<ModelArmorConfigMeta>();
            for (int i = 0; i < node.ChildNodes.Count; i++)
            {
                var child = node.ChildNodes[i];
                switch (child.Name)
                {
                    case "model":
                        var meta = ModelMeta.FromXmlNode(child, defaultNsp);
                        if (meta != null)
                            metas.Add(meta);
                        break;
                    case "armorconfig":
                        var armorConfig = ModelArmorConfigMeta.FromXmlNode(child, defaultNsp);
                        if (armorConfig != null)
                            armorConfigs.Add(armorConfig);
                        break;
                }
            }
            return new ModelMetaList(metas.ToArray(), armorConfigs.ToArray());
        }
    }
}
