#nullable enable // autogenerated

using System.Collections.Generic;
using System.Xml;

namespace MVZ2.Metas
{
    public class SoundMetaList
    {
        public SoundMeta[] metas;

        public SoundMetaList(SoundMeta[] metas)
        {
            this.metas = metas;
        }

        public static SoundMetaList FromXmlNode(XmlNode node, string defaultNsp)
        {
            var sounds = new List<SoundMeta>();
            for (int i = 0; i < node.ChildNodes.Count; i++)
            {
                var meta = SoundMeta.FromXmlNode(node.ChildNodes[i], defaultNsp);
                if (meta != null)
                    sounds.Add(meta);
            }
            return new SoundMetaList(sounds.ToArray());
        }
    }
}
