#nullable enable // autogenerated

using System.Collections.Generic;
using System.Xml;

namespace MVZ2.Metas
{
    public class SpawnMetaList
    {
        public SpawnMeta[] Metas;

        public SpawnMetaList(SpawnMeta[] metas)
        {
            Metas = metas;
        }

        public static SpawnMetaList FromXmlNode(XmlNode node, string defaultNsp)
        {
            var metas = new List<SpawnMeta>();
            for (int i = 0; i < node.ChildNodes.Count; i++)
            {
                var meta = SpawnMeta.FromXmlNode(node.ChildNodes[i], defaultNsp);
                if (meta != null)
                    metas.Add(meta);
            }
            return new SpawnMetaList(metas.ToArray());
        }
    }
}
