#nullable enable // autogenerated

using System.Xml;
using MVZ2.IO;

namespace MVZ2.Metas
{
    public class StatEntryMeta
    {
        public string ID { get; private set; }
        public string Name { get; private set; } = string.Empty;

        public StatEntryMeta(string iD)
        {
            ID = iD;
        }
        public static StatEntryMeta? FromXmlNode(XmlNode node, string defaultNsp)
        {
            var id = node.GetAttribute("id");
            if (string.IsNullOrEmpty(id))
            {
                Log.LogError($"The {nameof(id)} of a {nameof(StatEntryMeta)} is invalid.");
                return null;
            }
            var name = node.GetAttribute("name") ?? string.Empty;
            return new StatEntryMeta(id)
            {
                Name = name,
            };
        }
    }
}
