#nullable enable // autogenerated

using System.Collections.Generic;
using System.Linq;
using System.Xml;

namespace MVZ2.Metas
{
    public class StatMetaList
    {
        public StatCategoryMeta[] categories;
        public StatEntryMeta[] entries;

        public StatMetaList(StatCategoryMeta[] categories, StatEntryMeta[] entries)
        {
            this.categories = categories;
            this.entries = entries;
        }

        public static StatMetaList FromXmlNode(XmlNode node, string defaultNsp)
        {
            var categories = new List<StatCategoryMeta>();
            var entries = new List<StatEntryMeta>();
            for (int i = 0; i < node.ChildNodes.Count; i++)
            {
                var childNode = node.ChildNodes[i];
                switch (childNode.Name)
                {
                    case "category":
                        var meta = StatCategoryMeta.FromXmlNode(node.ChildNodes[i], defaultNsp);
                        if (meta != null)
                            categories.Add(meta);
                        break;
                    case "entry":
                        var entry = StatEntryMeta.FromXmlNode(node.ChildNodes[i], defaultNsp);
                        if (entry != null)
                            entries.Add(entry);
                        break;
                }
            }
            return new StatMetaList(categories.ToArray(), entries.ToArray());
        }
    }
}
