#nullable enable // autogenerated

using UnityEngine;

namespace MVZ2.Models
{
    [ExecuteAlways]
    public class AnimationGameObjectToggler : MonoBehaviour
    {
        private void Update()
        {
            UpdateEnable();
        }
        public void UpdateEnable()
        {
            if (gameObjectA.Exists())
                gameObjectA.SetActive(enableB < 0.99f);
            if (gameObjectB.Exists())
                gameObjectB.SetActive(enableB >= 0.99f);
        }
        [SerializeField]
        private float enableB;
        [SerializeField]
        private GameObject? gameObjectA;
        [SerializeField]
        private GameObject? gameObjectB;
    }
}
