#nullable enable // autogenerated

using UnityEngine;

namespace MVZ2.Models
{
    public class ModelUpdater : MonoBehaviour
    {
        void Update()
        {
            if (model.Exists())
            {
                var deltaTime = Time.deltaTime;
                model.UpdateFrame(deltaTime);
                model.UpdateAnimators(deltaTime);
            }
        }
        private void FixedUpdate()
        {
            if (model.Exists())
            {
                model.UpdateFixed();
            }
        }

        public Model? model;
    }
}
