#nullable enable // autogenerated

using System.Collections.Generic;
using System.Xml;
using MVZ2.Unlocks;

namespace MVZ2.Metas
{
    public class UnlockMetaList
    {
        public UnlockGroupMeta[] groups;

        public UnlockMetaList(UnlockGroupMeta[] groups)
        {
            this.groups = groups;
        }

        public static UnlockMetaList FromXmlNode(XmlNode node, string defaultNsp)
        {
            var metas = new List<UnlockGroupMeta>();
            for (int i = 0; i < node.ChildNodes.Count; i++)
            {
                var childNode = node.ChildNodes[i];
                switch (childNode.Name)
                {
                    case "group":
                        var meta = UnlockGroupMeta.FromXmlNode(node.ChildNodes[i], defaultNsp);
                        if (meta != null)
                        {
                            metas.Add(meta);
                        }
                        break;
                }
            }
            return new UnlockMetaList(metas.ToArray());
        }
    }
}
