#nullable enable // autogenerated

using System.Xml;
using MVZ2.IO;
using MVZ2.Metas;

namespace MVZ2.Unlocks
{
    public class UnlockGroupMeta
    {
        public UnlockGroupMeta(string id)
        {
            ID = id;
        }

        public string ID { get; private set; }
        public XMLConditionList? Conditions { get; private set; }
        public static UnlockGroupMeta? FromXmlNode(XmlNode node, string defaultNsp)
        {
            var id = node.GetAttribute("id");
            if (string.IsNullOrEmpty(id))
            {
                Log.LogError("The ID of an UnlockGroupMeta is invalid.");
                return null;
            }
            XmlNode? conditionsNode = node["conditions"];
            XMLConditionList? unlock = null;
            if (conditionsNode != null)
            {
                unlock = XMLConditionList.FromXmlNode(conditionsNode, defaultNsp);
            }
            return new UnlockGroupMeta(id)
            {
                Conditions = unlock,
            };
        }
    }
}
