#nullable enable // autogenerated

using UnityEngine;

namespace Tools.Mathematics
{
    // 立方体（轴对齐）
    public struct AABBCube : IConvexShape
    {
        public Bounds bounds;

        public AABBCube(Bounds bounds)
        {
            this.bounds = bounds;
        }

        public Vector3 GetFarthestPointInDirection(Vector3 direction)
        {
            return new Vector3(
                direction.x > 0 ? bounds.max.x : bounds.min.x,
                direction.y > 0 ? bounds.max.y : bounds.min.y,
                direction.z > 0 ? bounds.max.z : bounds.min.z
            );
        }

        public Vector3 GetCenter() => bounds.center;
    }
}