#nullable enable // autogenerated

using UnityEngine;

namespace Tools.Mathematics
{
    // 线段
    public struct Segment : IConvexShape
    {
        public Vector3 start;
        public Vector3 end;
        public Segment(Vector3 start, Vector3 end)
        {
            this.start = start;
            this.end = end;
        }

        public Vector3 GetFarthestPointInDirection(Vector3 direction)
        {
            float dotStart = Vector3.Dot(start, direction);
            float dotEnd = Vector3.Dot(end, direction);
            return dotStart > dotEnd ? start : end;
        }

        public Vector3 GetCenter() => (start + end) * 0.5f;
    }
}