#nullable enable // autogenerated

using MVZ2.GameContent.Damages;
using MVZ2.Vanilla.Callbacks;
using MVZ2.Vanilla.Entities;
using PVZEngine.Armors;
using PVZEngine.Callbacks;
using PVZEngine.Damages;
using PVZEngine.Entities;
using PVZEngine.Level;

namespace MVZ2.GameContent.Armors
{
    [ArmorBehaviourDefinition(VanillaArmorBehaviourNames.destroyAfterHalfHP)]
    public class DestroyAfterHalfHP : ArmorBehaviourDefinition
    {
        public DestroyAfterHalfHP(string nsp, string name) : base(nsp, name)
        {
            AddTrigger(VanillaLevelCallbacks.POST_ENTITY_TAKE_DAMAGE, PostEntityTakeDamageCallback);
        }
        private void PostEntityTakeDamageCallback(VanillaLevelCallbacks.PostTakeDamageParams param, CallbackResult result)
        {
            var output = param.output;
            var entity = output.Entity;
            if (entity.Health > entity.GetMaxHealth() * 0.5f)
                return;
            var armor = entity.GetMainArmor();
            if (armor == null || !armor.Definition.HasBehaviour(this))
                return;
            var source = output.BodyResult?.Source;
            armor.Destroy(new ArmorDestroyInfo(entity, armor, armor.Slot, new DamageEffectList(VanillaDamageEffects.SELF_DAMAGE), source, null));
        }
    }
}
