#nullable enable // autogenerated

using System.Collections.Generic;
using MVZ2.GameContent.Buffs;
using MVZ2Logic;
using MVZ2Logic.Artifacts;
using PVZEngine.Auras;
using PVZEngine.Buffs;

namespace MVZ2.GameContent.Artifacts
{
    [ArtifactDefinition(VanillaArtifactNames.sorcerersScroll)]
    public class SorcerersScroll : ArtifactDefinition
    {
        public SorcerersScroll(string nsp, string name) : base(nsp, name)
        {
            AddAura(new StarshardAura());
        }
        public class StarshardAura : AuraEffectDefinition
        {
            public StarshardAura() : base(VanillaBuffID.Level.sorcerersScrollStarshard)
            {
            }

            public override void GetAuraTargets(AuraEffect auraEffect, List<IBuffTarget> results)
            {
                results.Add(auraEffect.Source.GetLevel());
            }
        }
    }
}
