#nullable enable // autogenerated

using MVZ2.GameContent.Damages;
using MVZ2.Vanilla.Entities;
using MVZ2.Vanilla.Properties;
using PVZEngine.Callbacks;
using PVZEngine.Damages;
using PVZEngine.Entities;
using PVZEngine.Level;
using UnityEngine;

namespace MVZ2.GameContent.Bosses
{
    [EntityBehaviourDefinition(VanillaEntityBehaviourNames.bossResistance)]
    public partial class BossResistance : EntityBehaviourDefinition
    {
        public BossResistance(string nsp, string name) : base(nsp, name)
        {
        }
        public override void Update(Entity entity)
        {
            base.Update(entity);

            var current = GetCurrentDamageAmount(entity);
            var decay = GetDecay(entity);
            SetCurrentDamageAmount(entity, Mathf.Max(0, current - decay));
        }
        public override void PreTakeDamage(DamageInput damageInfo, CallbackResult result)
        {
            base.PreTakeDamage(damageInfo, result);
            var entity = damageInfo.Entity;

            var max = GetMaxDamageAmount(entity);
            var current = GetCurrentDamageAmount(entity);
            var currentDamageLimit = Mathf.Max(0, max - current);
            if (damageInfo.Amount > currentDamageLimit && !damageInfo.HasEffect(VanillaDamageEffects.BYPASS_BOSS_ARMOR))
            {
                damageInfo.SetAmount(currentDamageLimit);
            }
            SetCurrentDamageAmount(entity, current + damageInfo.Amount);
        }
        public static float GetCurrentDamageAmount(Entity entity) => entity.GetBehaviourField<float>(PROP_CURRENT_DAMAGE_AMOUNT);
        public static void SetCurrentDamageAmount(Entity entity, float value) => entity.SetBehaviourField(PROP_CURRENT_DAMAGE_AMOUNT, value);
        public static float GetMaxDamageAmount(Entity entity) => entity.GetBehaviourField<float>(PROP_MAX_DAMAGE_AMOUNT);
        public static void SetMaxDamageAmount(Entity entity, float value) => entity.SetBehaviourField(PROP_MAX_DAMAGE_AMOUNT, value);
        public static float GetDecay(Entity entity) => entity.GetBehaviourField<float>(PROP_DECAY);
        public static void SetDecay(Entity entity, float value) => entity.SetBehaviourField(PROP_DECAY, value);
        private static readonly VanillaEntityPropertyMeta<float> PROP_CURRENT_DAMAGE_AMOUNT = new VanillaEntityPropertyMeta<float>("current_damage_amount");
        private static readonly VanillaEntityPropertyMeta<float> PROP_MAX_DAMAGE_AMOUNT = new VanillaEntityPropertyMeta<float>("max_damage_amount", 600);
        private static readonly VanillaEntityPropertyMeta<float> PROP_DECAY = new VanillaEntityPropertyMeta<float>("decay", 40);
    }
}
