#nullable enable // autogenerated

using MVZ2.Vanilla.Entities;
using PVZEngine.Callbacks;
using PVZEngine.Damages;
using PVZEngine.Entities;
using PVZEngine.Level;

namespace MVZ2.GameContent.Bosses
{
    [EntityBehaviourDefinition(VanillaEntityBehaviourNames.bossResistance_Seija)]
    public partial class BossResistance_Seija : BossResistance
    {
        public BossResistance_Seija(string nsp, string name) : base(nsp, name)
        {
        }
        public override void PreTakeDamage(DamageInput damageInfo, CallbackResult result)
        {
            var boss = damageInfo.Entity;
            if (damageInfo.Amount >= 600)
            {
                if (Seija.CanUseFabric(boss))
                {
                    Seija.UseFabric(boss);
                }
            }
            if (boss.State == Seija.STATE_FABRIC)
            {
                result.SetFinalValue(false);
                return;
            }
            base.PreTakeDamage(damageInfo, result);
        }
    }
}
