#nullable enable // autogenerated

using MVZ2.Vanilla.Entities;
using PVZEngine.Callbacks;
using PVZEngine.Damages;
using PVZEngine.Entities;
using PVZEngine.Level;

namespace MVZ2.GameContent.Bosses
{
    [EntityBehaviourDefinition(VanillaEntityBehaviourNames.bossResistance_TheGiant)]
    public partial class BossResistance_TheGiant : BossResistance
    {
        public BossResistance_TheGiant(string nsp, string name) : base(nsp, name)
        {
        }
        public override void PreTakeDamage(DamageInput damageInfo, CallbackResult result)
        {
            var entity = damageInfo.Entity;
            var malleable = TheGiant.GetMalleable(entity);
            if (malleable >= 0)
            {
                damageInfo.Multiply(1 - malleable / TheGiant.MAX_MALLEABLE_DAMAGE);
            }
            base.PreTakeDamage(damageInfo, result);
        }
    }
}
