#nullable enable // autogenerated

using MVZ2.Vanilla.Properties;
using PVZEngine.Buffs;
using PVZEngine.Level;
using PVZEngine.Modifiers;

namespace MVZ2.GameContent.Buffs.Enemies
{
    [BuffDefinition(VanillaBuffNames.Boss.bossRevenge)]
    public class BossRevengeBuff : BuffDefinition
    {
        public BossRevengeBuff(string nsp, string name) : base(nsp, name)
        {
            AddModifier(new MaxHealthModifier(NumberOperator.Multiply, PROP_HEALTH_MULTIPLIER));
        }
        public static readonly VanillaBuffPropertyMeta<float> PROP_HEALTH_MULTIPLIER = new VanillaBuffPropertyMeta<float>("health_multiplier", 1.5f);
    }
}
