#nullable enable // autogenerated

using MVZ2.Vanilla.Properties;
using PVZEngine.Buffs;
using PVZEngine.Level;

namespace MVZ2.GameContent.Buffs.Contraptions
{
    [BuffDefinition(VanillaBuffNames.Contraption.elasticCloudBounceCooldown)]
    public class ElasticCloudBounceCooldownBuff : BuffDefinition
    {
        public ElasticCloudBounceCooldownBuff(string nsp, string name) : base(nsp, name)
        {
        }
        public override void PostUpdate(Buff buff)
        {
            base.PostUpdate(buff);
            var timeout = GetTimeout(buff);
            timeout--;
            SetTimeout(buff, timeout);
            if (timeout <= 0)
            {
                buff.Remove();
            }
        }
        public static int GetTimeout(Buff buff) => buff.GetProperty<int>(PROP_TIMEOUT);
        public static void SetTimeout(Buff buff, int value) => buff.SetProperty(PROP_TIMEOUT, value);
        public static long GetTargetID(Buff buff) => buff.GetProperty<long>(PROP_TARGET_ID);
        public static void SetTargetID(Buff buff, long value) => buff.SetProperty(PROP_TARGET_ID, value);
        public static readonly VanillaBuffPropertyMeta<int> PROP_TIMEOUT = new VanillaBuffPropertyMeta<int>("timeout");
        public static readonly VanillaBuffPropertyMeta<long> PROP_TARGET_ID = new VanillaBuffPropertyMeta<long>("target_id");
    }
}
