#nullable enable // autogenerated

using MVZ2.Vanilla.Properties;
using PVZEngine.Buffs;

namespace MVZ2.GameContent.Buffs.Enemies
{
    [PVZEngine.Level.BuffDefinition(VanillaBuffNames.Enemy.AttackSpeed)]
    public class AttackSpeedBuff : BuffDefinition
    {
        public AttackSpeedBuff(string nsp, string name) : base(nsp, name)
        {
        }
        public override void PostAdd(Buff buff)
        {
            base.PostAdd(buff);
            buff.SetProperty(PROP_ATTACK_SPEED_MULTIPLIER, 2f);
        }
        public static readonly VanillaBuffPropertyMeta<float> PROP_ATTACK_SPEED_MULTIPLIER = new VanillaBuffPropertyMeta<float>("AttackSpeedMultiplier", 1f);
    }
}
