#nullable enable // autogenerated

using MVZ2.GameContent.Enemies;
using MVZ2.Vanilla.Properties;
using PVZEngine;
using PVZEngine.Buffs;
using PVZEngine.Entities;

namespace MVZ2.GameContent.Buffs.Enemies
{
    [PVZEngine.Level.BuffDefinition(VanillaBuffNames.Enemy.BoneWall)]
    public class BoneWallBuff : BuffDefinition
    {
        public BoneWallBuff(string nsp, string name) : base(nsp, name)
        {
        }
        public override void PostAdd(Buff buff)
        {
            base.PostAdd(buff);
            buff.SetProperty(PROP_TIMEOUT, MAX_TIMEOUT);
        }
        public override void PostUpdate(Buff buff)
        {
            base.PostUpdate(buff);
            var timeout = buff.GetProperty<int>(PROP_TIMEOUT);
            timeout--;
            buff.SetProperty(PROP_TIMEOUT, timeout);
            if (timeout <= 0)
            {
                TransformToSkeleton(buff);
                buff.Remove();
            }
        }
        private void TransformToSkeleton(Buff buff)
        {
            var entity = buff.GetEntity();
            if (entity == null)
                return;
            entity.ChangeModel(new NamespaceID("mvz2", "skeleton"));
        }
        public static readonly VanillaBuffPropertyMeta<int> PROP_TIMEOUT = new VanillaBuffPropertyMeta<int>("Timeout");
        public const int MAX_TIMEOUT = 600;
    }
}
