#nullable enable // autogenerated

using MVZ2.GameContent.Damages;
using MVZ2.GameContent.Models;
using MVZ2.Vanilla.Entities;
using MVZ2.Vanilla.Models;
using MVZ2.Vanilla.Properties;
using MVZ2Logic.Models;
using PVZEngine.Buffs;
using PVZEngine.Damages;
using PVZEngine.Level;
using System.Collections.Generic;
using MVZ2.Vanilla.Callbacks;
using PVZEngine.Callbacks;


namespace MVZ2.GameContent.Buffs.Enemies
{
    [BuffDefinition(VanillaBuffNames.Entity.DeathMark)]
    public class DeathMarkBuff : BuffDefinition
    {
        public DeathMarkBuff(string nsp, string name) : base(nsp, name)
        {
            //插入模型
            AddTrigger(VanillaLevelCallbacks.PRE_ENTITY_TAKE_DAMAGE, PreEntityTakeDamageCallback);
            AddModelInsertion(LogicModelHelper.ANCHOR_CENTER, VanillaModelKeys.DeathMarkParticles, VanillaModelID.DeathMarkParticles);
        }

        private void PreEntityTakeDamageCallback(VanillaLevelCallbacks.PreTakeDamageParams param, CallbackResult callbackResult)
        {
            var damageInfo = param.input;
            var entity = damageInfo.Entity;

            if (entity == null)
                return;

            buffBuffer.Clear();
            entity.GetBuffs<DeathMarkBuff>(buffBuffer);
            if (buffBuffer.Count == 0)
                return;

            damageInfo.Multiply(1.5f);
        }

        private List<Buff> buffBuffer = new List<Buff>();

        public override void PostUpdate(Buff buff)
        {
            base.PostUpdate(buff);

            var entity = buff.GetEntity();
            if (entity != null && !entity.IsDead)
            {

                float damage = buff.GetProperty<float>(PROP_DAMAGE_AMOUNT);
                entity.TakeDamage(damage, new DamageEffectList(VanillaDamageEffects.MUTE, VanillaDamageEffects.NO_DAMAGE_BLINK, VanillaDamageEffects.IGNORE_ARMOR), entity);
            }

            // Buff持续时间
            var timeout = buff.GetProperty<int>(PROP_TIMEOUT);
            timeout--;
            buff.SetProperty(PROP_TIMEOUT, timeout);

            if (timeout <= 0)
            {
                buff.Remove();
            }
        }

        // PropertyKey
        public static readonly VanillaBuffPropertyMeta<float> PROP_DAMAGE_AMOUNT = new VanillaBuffPropertyMeta<float>("DamageAmount", 1f); //每帧造成伤害
        public static readonly VanillaBuffPropertyMeta<int> PROP_TIMEOUT = new VanillaBuffPropertyMeta<int>("Timeout");       //持续时间
    }
}
