#nullable enable // autogenerated

using MVZ2.GameContent.Effects;
using MVZ2.Vanilla.Entities;
using MVZ2.Vanilla.Properties;
using PVZEngine.Buffs;
using PVZEngine.Entities;
using PVZEngine.Level;
using PVZEngine.Modifiers;
using Tools;
using UnityEngine;


namespace MVZ2.GameContent.Buffs.Enemies
{
    [BuffDefinition(VanillaBuffNames.Enemy.SoulFreeze)]
    public class SoulFreezeBuff : BuffDefinition
    {
        public SoulFreezeBuff(string nsp, string name) : base(nsp, name)
        {
            AddModifier(new BooleanModifier(VanillaEntityProps.AI_FROZEN, true));
            AddModifier(new ColorModifier(EngineEntityProps.TINT, new Color(0.5f, 0.5f, 1, 1f)));
        }
        public override void PostAdd(Buff buff)
        {
            base.PostAdd(buff);
            UpdateFreezeStars(buff);
            buff.SetProperty(SoulFreezeBuff.PROP_TIMER, new FrameTimer(0));
        }
        public override void PostRemove(Buff buff)
        {
            base.PostRemove(buff);
            var starsID = GetFreezeStars(buff);
            var stars = starsID?.GetEntity(buff.Level);
            if (stars != null && stars.Exists())
            {
                stars.Remove();
            }
        }
        public override void PostUpdate(Buff buff)
        {
            base.PostUpdate(buff);
            UpdateFreeze(buff);
            UpdateFreezeStars(buff);
        }
        private void UpdateFreeze(Buff buff)
        {
            var timer = buff.GetProperty<FrameTimer>(PROP_TIMER);
            if (timer == null)
            {
                buff.Remove();
                return;
            }
            timer.Run();
            if (timer.Expired)
            {
                buff.Remove();
                return;
            }
            var entity = buff.GetEntity();
            if (entity == null || !entity.Exists() || entity.IsDead)
            {
                buff.Remove();
                return;
            }
        }
        private void UpdateFreezeStars(Buff buff)
        {
            var entity = buff.GetEntity();
            if (entity == null)
                return;
            var starsID = GetFreezeStars(buff);
            var stars = starsID?.GetEntity(entity.Level);
            if (stars == null || !stars.Exists())
            {
                var spawnPos = entity.Position;
                stars = entity.Spawn(VanillaEffectID.SoulFreeze, spawnPos)?.Let(e =>
                {
                    e.SetParent(entity);  // 关键：设置父实体  
                    SetFreezeStars(buff, new EntityID(e));
                });
            }
        }
        public static void SetFreezeTime(Buff buff, int value)
        {
            var timer = buff.GetProperty<FrameTimer>(PROP_TIMER);
            if (timer == null)
                return;
            timer.ResetTime(value);
        }
        public static EntityID? GetFreezeStars(Buff buff) => buff.GetProperty<EntityID>(PROP_Freeze_STARS);
        public static void SetFreezeStars(Buff buff, EntityID value) => buff.SetProperty(PROP_Freeze_STARS, value);
        public static readonly VanillaBuffPropertyMeta<FrameTimer> PROP_TIMER = new VanillaBuffPropertyMeta<FrameTimer>("Timer");
        public static readonly VanillaBuffPropertyMeta<EntityID> PROP_Freeze_STARS = new VanillaBuffPropertyMeta<EntityID>("FreezeStars");
    }
}
