#nullable enable // autogenerated

using MVZ2.Vanilla.Level;
using MVZ2.Vanilla.Properties;
using PVZEngine;
using PVZEngine.Buffs;
using PVZEngine.Level;
using Tools;

namespace MVZ2.GameContent.Buffs.Enemies
{
    [BuffDefinition(VanillaBuffNames.Level.redDragonStage)]
    public class RedDragonStageBuff : BuffDefinition
    {
        public RedDragonStageBuff(string nsp, string name) : base(nsp, name)
        {
        }
        public override void OnCreate(Buff buff)
        {
            base.OnCreate(buff);
            buff.SetProperty(PROP_THUNDER_TIMER, new FrameTimer(MAX_THUNDER_TIMEOUT));
        }
        public override void PostUpdate(Buff buff)
        {
            base.PostUpdate(buff);
            var timer = buff.GetProperty<FrameTimer>(PROP_THUNDER_TIMER);
            if (timer.RunToExpiredAndNotNull())
            {
                timer.Reset();
                buff.Level.Thunder();
            }
        }
        public static readonly VanillaBuffPropertyMeta<FrameTimer> PROP_THUNDER_TIMER = new VanillaBuffPropertyMeta<FrameTimer>("thunder_timer");
        public const int MAX_THUNDER_TIMEOUT = 300;
    }
}
