#nullable enable // autogenerated

using MVZ2.GameContent.Seeds;
using MVZ2.Vanilla.Level;
using MVZ2.Vanilla.Properties;
using PVZEngine.Buffs;
using PVZEngine.Level;
using PVZEngine.Modifiers;

namespace MVZ2.GameContent.Buffs.Level
{
    [BuffDefinition(VanillaBuffNames.Level.slendermanDisable)]
    public class SlendermanDisableBuff : BuffDefinition
    {
        public SlendermanDisableBuff(string nsp, string name) : base(nsp, name)
        {
            AddModifier(new NamespaceIDModifier(VanillaLevelProps.PICKAXE_DISABLE_ID, SetOperator.Set, VanillaBlueprintErrors.decrepify));
            AddModifier(new BooleanModifier(VanillaLevelProps.PICKAXE_DISABLE_ICON, true));
            AddModifier(new NamespaceIDModifier(VanillaLevelProps.STARSHARD_DISABLE_ID, SetOperator.Set, VanillaBlueprintErrors.decrepify));
            AddModifier(new BooleanModifier(VanillaLevelProps.STARSHARD_DISABLE_ICON, true));
        }
        public override void PostAdd(Buff buff)
        {
            base.PostAdd(buff);
            buff.SetProperty(PROP_CONTRAPTION_TIMEOUT, CONTRAPTION_DISABLE_TIMEOUT);
            buff.SetProperty(PROP_CARD_TIMEOUT, CARD_DISABLE_TIMEOUT);
        }
        public override void PostUpdate(Buff buff)
        {
            base.PostUpdate(buff);
            var contraptionTimeout = buff.GetProperty<int>(PROP_CONTRAPTION_TIMEOUT);
            if (contraptionTimeout > 0)
            {
                contraptionTimeout--;
                buff.SetProperty(PROP_CONTRAPTION_TIMEOUT, contraptionTimeout);
            }
            var cardTimeout = buff.GetProperty<int>(PROP_CARD_TIMEOUT);
            if (cardTimeout > 0)
            {
                cardTimeout--;
                buff.SetProperty(PROP_CARD_TIMEOUT, cardTimeout);
            }
            if (contraptionTimeout <= 0 && cardTimeout <= 0)
            {
                buff.Remove();
            }
        }
        public static readonly VanillaBuffPropertyMeta<int> PROP_CONTRAPTION_TIMEOUT = new VanillaBuffPropertyMeta<int>("ContraptionTimeout");
        public static readonly VanillaBuffPropertyMeta<int> PROP_CARD_TIMEOUT = new VanillaBuffPropertyMeta<int>("CardTimeout");
        public const int CONTRAPTION_DISABLE_TIMEOUT = 600;
        public const int CARD_DISABLE_TIMEOUT = 480;
    }
}
