#nullable enable // autogenerated

using MVZ2.Vanilla.Contraptions;
using PVZEngine.Entities;

namespace MVZ2.Vanilla.Entities
{
    public abstract class ContraptionTriggerBehaviour : EntityBehaviourDefinition, ITriggerableContraption
    {
        public ContraptionTriggerBehaviour(string nsp, string name) : base(nsp, name)
        {
        }
        public virtual bool CanTrigger(Entity entity)
        {
            return entity.IsTriggerActive() && !entity.IsAIFrozen();
        }
        public virtual void Trigger(Entity entity)
        {
        }
    }
}