#nullable enable // autogenerated

using MVZ2.GameContent.Buffs;
using MVZ2.Vanilla.Entities;
using PVZEngine.Buffs;
using PVZEngine.Entities;
using PVZEngine.Level;
using UnityEngine;

namespace MVZ2.GameContent.Contraptions
{
    [EntityBehaviourDefinition(VanillaEntityBehaviourNames.elasticCloud_Evocation)]
    public class ElasticCloud_Evocation : ContraptionEvokeBehaviour
    {
        public ElasticCloud_Evocation(string nsp, string name) : base(nsp, name)
        {
        }
        public override bool CanEvoke(Entity entity)
        {
            if (entity.HasBuff(VanillaBuffID.Contraption.elasticCloudEvocation))
                return false;
            return base.CanEvoke(entity);
        }
        public override void Evoke(Entity entity)
        {
            base.Evoke(entity);
            entity.Velocity += Vector3.up * 30;
            entity.AddBuff(VanillaBuffID.Contraption.elasticCloudEvocation);
        }
    }
}
