#nullable enable // autogenerated

using MVZ2.GameContent.Bosses;
using MVZ2.Vanilla.Detections;
using MVZ2.Vanilla.Entities;
using PVZEngine.Entities;
using UnityEngine;

namespace MVZ2.GameContent.Detections
{
    public class RedDragonSpitDetector : Detector
    {
        protected override Bounds GetDetectionBounds(Entity self)
        {
            var source = RedDragon.GetSpitSourcePosition(self);
            var projectileSize = Vector3.one * 32;

            var sizeX = 800;
            var sizeY = 1000;
            var sizeZ = projectileSize.z;
            var centerX = source.x + sizeX * 0.5f * self.GetFacingX();
            var centerY = source.y + sizeY * 0.5f;
            var centerZ = source.z;


            return new Bounds(new Vector3(centerX, centerY, centerZ), new Vector3(sizeX, sizeY, sizeZ));
        }
    }
}
