#nullable enable // autogenerated

using MVZ2.Vanilla.Entities;
using PVZEngine.Entities;
using PVZEngine.Level;

namespace MVZ2.GameContent.Effects
{
    [EntityBehaviourDefinition(VanillaEffectNames.skywardBeaconTarget)]
    public class SkywardBeaconTarget : EffectBehaviour
    {
        public SkywardBeaconTarget(string nsp, string name) : base(nsp, name)
        {
        }
        public override void Update(Entity entity)
        {
            base.Update(entity);
            UpdateModel(entity);
        }
        private void UpdateModel(Entity entity)
        {
            entity.SetModelProperty("ShowLine", true);
            var parent = entity.Parent;
            if (parent.ExistsAndAlive())
            {
                entity.SetModelProperty("Dest", parent.Position);
            }
        }
    }
}