#nullable enable // autogenerated

using MVZ2.GameContent.Damages;
using MVZ2.Vanilla.Audios;
using MVZ2.Vanilla.Entities;
using MVZ2Logic.Level;
using PVZEngine.Damages;
using PVZEngine.Entities;
using PVZEngine.Level;
using UnityEngine;

namespace MVZ2.GameContent.Effects
{
    [EntityBehaviourDefinition(VanillaEffectNames.tornado)]
    public class Tornado : EffectBehaviour, IBeBlownBehaviour
    {
        public Tornado(string nsp, string name) : base(nsp, name)
        {
        }
        public override void Init(Entity entity)
        {
            base.Init(entity);
            entity.CollisionMaskHostile |= EntityCollisionHelper.MASK_VULNERABLE;
            entity.Level.AddLoopSoundEntity(VanillaSoundID.tornado, entity.ID);
        }
        public override void Update(Entity entity)
        {
            base.Update(entity);
            entity.SetTint(new Color(1, 1, 1, Mathf.Clamp01(entity.Timeout / 30f)));
        }
        public override void PostCollision(EntityCollision collision, int state)
        {
            base.PostCollision(collision, state);
            var other = collision.Other;
            var self = collision.Entity;
            if (!other.IsVulnerableEntity())
                return;
            if (!self.IsHostile(other))
                return;
            if (state == EntityCollisionHelper.STATE_EXIT)
                return;

            var damage = self.GetDamage();
            var effectList = new DamageEffectList(VanillaDamageEffects.MUTE);
            collision.OtherCollider.TakeDamage(damage, effectList, self);
        }

        public void BeBlown(Entity entity, Entity source)
        {
            var newVelocity = source.IsFacingLeft() ? Vector3.left : Vector3.right;
            newVelocity *= entity.Velocity.magnitude;
            entity.Velocity = newVelocity;
        }
    }
}