#nullable enable // autogenerated

using MVZ2.Vanilla.Entities;
using PVZEngine.Entities;
using PVZEngine.Level;

namespace MVZ2.GameContent.Effects
{
    [EntityBehaviourDefinition(VanillaEffectNames.windSpeedline)]
    public class WindSpeedline : EffectBehaviour
    {

        #region 公有方法
        public WindSpeedline(string nsp, string name) : base(nsp, name)
        {
        }
        public override void Init(Entity entity)
        {
            base.Init(entity);
            UpdateModelSize(entity);
        }
        public override void Update(Entity entity)
        {
            base.Update(entity);
            UpdateModelSize(entity);
            if (!entity.Parent.ExistsAndAlive())
            {
                entity.Remove();
            }
            else
            {
                entity.SetFlipX(entity.Parent.IsFlipX());
            }
        }
        private void UpdateModelSize(Entity entity)
        {
            var size = entity.GetScaledSize();
            size.y = 80f;
            entity.SetModelProperty("Size", size);
        }
        #endregion
    }
}