#nullable enable // autogenerated

using PVZEngine.Entities;
using PVZEngine.Level;

namespace MVZ2.Vanilla.Entities
{
    [EntityBehaviourDefinition(VanillaEntityBehaviourNames.stopParticlesWithoutParent)]
    public class StopParticlesWithoutParentBehaviour : EntityBehaviourDefinition
    {
        public StopParticlesWithoutParentBehaviour(string nsp, string name) : base(nsp, name)
        {
        }
        public override void Update(Entity entity)
        {
            base.Update(entity);
            entity.SetModelProperty("ParticleStopped", !entity.Parent.ExistsAndAlive());
        }
    }
}
