#nullable enable // autogenerated

using MVZ2.GameContent.Buffs.Enemies;
using MVZ2.GameContent.Damages;
using MVZ2.Vanilla.Enemies;
using MVZ2.Vanilla.Entities;
using MVZ2.Vanilla.Level;
using PVZEngine.Buffs;
using PVZEngine.Damages;
using PVZEngine.Entities;
using PVZEngine.Level;
using PVZEngine.Callbacks;
using MVZ2.Vanilla.Properties;

namespace MVZ2.GameContent.Enemies
{
    [EntityBehaviourDefinition(VanillaEnemyNames.TorchKongfuZombie)]
    public class TorchKongfuZombie : MonkZombie
    {
        public TorchKongfuZombie(string nsp, string name) : base(nsp, name)
        {
        }

        // 在 TorchKongfuZombie 类中添加属性定义  
        public static readonly VanillaEntityPropertyMeta<bool> PROP_TORCH_LIT =
            new VanillaEntityPropertyMeta<bool>("TorchLit");

        // 在 Init 方法中初始化火把状态  
        public override void Init(Entity entity)
        {
            base.Init(entity);
            entity.SetProperty(PROP_TORCH_LIT, true); // 默认火把是点燃的  
        }

        // 添加伤害处理方法  
        public override void PreTakeDamage(DamageInput input, CallbackResult result)
        {
            base.PreTakeDamage(input, result);

            if ((input.HasEffect(VanillaDamageEffects.ICE)) || (input.HasEffect(VanillaDamageEffects.SoulColdFire)))
            {
                // 冰系伤害熄灭火把  
                input.Entity.SetProperty(PROP_TORCH_LIT, false);
            }
            else if (input.HasEffect(VanillaDamageEffects.FIRE))
            {
                // 火系伤害点燃火把  
                input.Entity.SetProperty(PROP_TORCH_LIT, true);
            }
        }

        // 修改 UpdateLogic 方法，只有火把点燃时才使用 FireAOE  
        protected override void UpdateLogic(Entity entity)
        {
            base.UpdateLogic(entity);
            entity.SetModelDamagePercent();

            if (entity.GetProperty<bool>(PROP_TORCH_LIT))
            {
                FireAOE(entity, 3.34f, entity.GetFaction());
            }
        }

        public static void FireAOE(Entity entity, float damage, int faction)
        {

            var range = 60;

            entity.Explode(
                entity.GetCenter(),
                range,
                faction,
                damage,
                new DamageEffectList(VanillaDamageEffects.DAMAGE_BOTH_ARMOR_AND_BODY, VanillaDamageEffects.MUTE, VanillaDamageEffects.FIRE)
            );


        }
        
    }
}
