#nullable enable // autogenerated

using System.Diagnostics.CodeAnalysis;
using MVZ2.Vanilla.Enemies;
using MVZ2.Vanilla.Entities;
using PVZEngine.Entities;
using PVZEngine.Level;

namespace MVZ2.GameContent.Enemies
{
    [EntityBehaviourDefinition(VanillaEntityBehaviourNames.spider_Melee)]
    public class Spider_MeleeBehaviour : EnemyMeleeBehaviour
    {
        public Spider_MeleeBehaviour(string nsp, string name) : base(nsp, name)
        {
        }
        protected override bool ValidateMeleeTarget(Entity enemy, [NotNullWhen(true)] Entity? target)
        {
            if (!base.ValidateMeleeTarget(enemy, target))
                return false;
            if (Spider.CanClimb(enemy, target))
                return false;
            return true;
        }
    }
}
