#nullable enable // autogenerated

using MVZ2.Vanilla.Enemies;
using MVZ2.Vanilla.Entities;
using PVZEngine.Entities;
using PVZEngine.Level;

namespace MVZ2.GameContent.Enemies
{
    [EntityBehaviourDefinition(VanillaEntityBehaviourNames.NightmareDisciple_State)]
    public class NightmareDisciple_State : EnemyStateBehaviour
    {
        public NightmareDisciple_State(string nsp, string name) : base(nsp, name)
        {
        }
        protected override int GetActiveState(Entity enemy)
        {
            var state = base.GetActiveState(enemy);
            if (enemy.Target.ExistsAndAlive())
            {
                var attackState = SkeletonMage.GetAttackState(enemy);
                if (attackState == SkeletonMage.ATTACK_STATE_CAST)
                {
                    state = STATE_CAST;
                }
                else
                {
                    state = STATE_RANGED_ATTACK;
                }
            }
            return state;
        }
    }
}
