#nullable enable // autogenerated

using MVZ2.Vanilla.Enemies;
using MVZ2.Vanilla.Entities;
using PVZEngine.Entities;
using PVZEngine.Level;

namespace MVZ2.GameContent.Enemies
{
    [EntityBehaviourDefinition(VanillaEntityBehaviourNames.ghast_State)]
    public class Ghast_StateBehaviour : EnemyStateBehaviour
    {
        public Ghast_StateBehaviour(string nsp, string name) : base(nsp, name)
        {
        }
        protected override int GetActiveState(Entity enemy)
        {
            if (enemy.Target.ExistsAndAlive())
            {
                return STATE_RANGED_ATTACK;
            }
            return base.GetActiveState(enemy);
        }
    }
}
