#nullable enable // autogenerated

using MVZ2.Vanilla.Enemies;
using MVZ2.Vanilla.Entities;
using PVZEngine.Entities;
using PVZEngine.Level;

namespace MVZ2.GameContent.Enemies
{
    [EntityBehaviourDefinition(VanillaEntityBehaviourNames.reverseSatellite_State)]
    public class ReverseSatellite_StateBehaviour : EnemyStateBehaviour
    {
        public ReverseSatellite_StateBehaviour(string nsp, string name) : base(nsp, name)
        {
        }
        protected override int GetActiveState(Entity enemy)
        {
            var state = base.GetActiveState(enemy);
            if (state == STATE_WALK && ReverseSatellite.IsLeft(enemy))
            {
                state = STATE_LEAVE;
            }
            return state;
        }
    }
}
